INSERT INTO [dbo].[Product] ([Id], [Sku], [Title], [IsNew], [Dimension], [Price], [PriceBig], [Description], [CategoryId], [Summary], [Materials], [Number], [IsPublished]) VALUES (8, N'cl1', N'Клатч кожаный PUNCH', 1, N'19x10,5x2,5см', 3100, 3450, N'Очень красивое и стильное изделие. Пользуется популярностью как у мужчин так и у женщин. Имеет четыре кармана для банковских карт или визиток. По центру расположено большое отделение, которое закрывается качественным замком на всю длину изделия. Имеется три отделения на всю длину изделия для купюр, документов или смартфона. В закрытом состоянии клатч фиксируется двумя магнитами. Для удобства ношения предусмотрена съемная ручка из двух слоев кожи с карабином, которую можно пристегнуть к боковой стороне клатча. Изготовлен из качественной натуральной кожи. Все швы выполнены вручную вощеной нитью.', 2, N'три отделения на всю длину изделия&одно отделение на всю длину на замке&четыре кармана для банковских карт или визиток&закрывается на два магнита&съемный ремешок-петля на карабине', N'натуральная кожа КРС&магниты, металлический замок, металлический карабин&вощеная нить&химия для кожи Fenice, средство для уреза Tokonole, воск
